class Solution:    def numTilings(self, n: int) -> int:        full_prev, full, missing = 1, 2, 1        if n == 1 or n == 0:            return 1        for i in range(3, n + 1):            current = full + full_prev + 2 * missing            missing = full_prev + missing            full_prev, full = full, current        return fulls1 = Solution()print(s1.numTilings(3))